 MODIF_PERSO For Personas ACTIVE
  BEFORE UPDATE POSITION 0
as 
  
declare temporal TSNOMBRE3x;
declare crlf char(2);
declare separador  char(2);  

    Begin
    If (New.modificacion Is null) Then 
      New.modificacion = current_timestamp;
    separador = '';
    Crlf = Ascii_Char(10); 
     
    Select Temporal, Separador
      From TraeCambio('nombre1', Old.Perso_Nombre1, 
                       New.Perso_Nombre1, :Temporal, :Separador) 
      Into :Temporal, :Separador;
    Select Temporal, Separador
      From TraeCambio('nombre2', Old.Perso_Nombre2, 
                       New.Perso_Nombre2, :Temporal, :Separador) 
      Into :Temporal, :Separador;
    Select Temporal, Separador
      From TraeCambio('nombre3', Old.Perso_Nombre3, 
                       New.Perso_Nombre3, :Temporal, :Separador) 
      Into :Temporal, :Separador;
    Select Temporal, Separador
      From TraeCambio('paterno', Old.Perso_Paterno, 
                       New.Perso_Paterno, :Temporal, :Separador) 
      Into :Temporal, :Separador;
    Select Temporal, Separador
      From TraeCambio('materno', Old.Perso_Materno, 
                       New.Perso_Materno, :Temporal, :Separador) 
      Into :Temporal, :Separador;
    Select Temporal, Separador
      From TraeCambio('nacionalidad', Old.Perso_Nacionalidad ,
                       New.Perso_Nacionalidad , :Temporal, :Separador) 
      Into :Temporal, :Separador;
    Select Temporal, Separador
      From TraeCambio('fecha Nacimiento', Old.Perso_Fecha_Nacimiento,
                       New.Perso_Fecha_Nacimiento, :Temporal, :Separador) 
      Into :Temporal, :Separador;  
    Select Temporal, Separador
      From TraeCambio('sexo', Old.Perso_Sexo,
                       New.Perso_Sexo  , :Temporal, :Separador) 
      Into :Temporal, :Separador;
   If (( :temporal is not null )  and (:Temporal <> '')) Then 
      Begin 
      Select Historia 
        From GeneraCambios (Old.Modificacion, Old.Autor,  
                            :Temporal,        Old.Historia ) 
        Into New.Historia;
      End
    End^
SET TERM ; ^

